/**
 * Adds two numbers together
 * @param  {number} x
 * @param  {number} y
 * @returns number
 */
export function add(x, y) {
    return x + y;
}
/**
 * Subtracts Y from X.
 * @param  {number} x
 * @param  {number} y
 * @returns number
 */
export function subtract(x, y) {
    return x - y;
}
/**
 * Squares a number.
 * @param  {number} x
 * @returns number
 */
export function square(x) {
    return Math.pow(x, 2);
}
/**
 * Cubes a number
 * @param  {number} x
 * @returns number
 */
export function cube(x) {
    return Math.pow(x, 3);
}
